mapper.select:
select  from cms_article 

	
		and title like "%"#{title}"%"
	
	
		and status = #{status}
	
	
		and user_id = #{userId}
	
	
		and hot = #{hot}
	
	
		and deleted = #{deleted}
	
	
		and channel_id = #{channelId}
	
	
		and category_id = #{categoryId}
	
	
		and content_type = #{contentType}
	

order by updated desc
